mapper.update:

update t_campus_event
set event_info = #{eventInfo,jdbcType=VARCHAR},
  event_img1 = #{eventImg1,jdbcType=VARCHAR},
  event_img2 = #{eventImg2,jdbcType=VARCHAR},
  event_img3 = #{eventImg3,jdbcType=VARCHAR}
where event_id = #{eventId,jdbcType=INTEGER}

mapper.select:

select event_id, event_info, event_img1, event_img2, event_img3, release_time
from t_campus_event
where event_id = #{eventId,jdbcType=INTEGER}
